<script setup>
import { ref } from "vue";
import {gsap} from 'gsap';
import { onMounted } from "vue";
import {CustomEase} from 'gsap/CustomEase';

gsap.registerPlugin(CustomEase);
CustomEase.create("hop", "M0,0 C0.124,0.382 0.071,1.051 0.283,1.03 0.44,1.014 0.416,0.177 0.559,0.316 0.734,0.486 0.696,0.188 0.779,0.176 0.847,0.166 0.868,0.091 0.899,0.062 0.938,0.026 0.979,0.064 1,0.066 ");



    const showTitle = ref(true);
    const beforeEnter = (el) => {
      el.style.transform = "translateY(-60px)";
      el.style.opacity = 0;
    };

  
    const enter = (el, done) => {
      gsap.to(el, {
        duration: 2,
        y: 0,
        opacity: 1,
        ease: "slow",
        onComplete: done,
      });
    };

// 

onMounted(() => {
  
 

gsap.fromTo(
    "#mouse-point",
    {
      opacity: 1,
      // y: "100%",
    },
    {
       duration: 3,
       opacity: 1,
      delay: 2,
       y: -1,
       repeat:-1,
       ease: "hop",
       scaleY:2,
      onComplete: () => {
        gsap.to("#mouse-point", {
        y:4,
          duration: 2,
          ease: "bounce",
          repeat:-1,
          yoyo: true,
        });
      },
      
    }
  );
  });

  
</script>

